hudson.tasks.Shell.command:
PROJECTSOURCE=OBS/${project}
# needs .oscrc with user,pass,trusted_prj # zypper ar http://download.opensuse.org/repositories/openSUSE:/Tools/SLE_11_SP2/openSUSE:Tools.repo # zypper ar http://download.opensuse.org/repositories/openSUSE:/Tools:/Unstable/SLE_11_SP2/openSUSE:Tools:Unstable.repo # zypper in osc obs-service-tar_scm obs-service-github_tarballs obs-service-recompress obs-service-git_tarballs obs-service-set_version [ -z "$PROJECTSOURCE" ] && ( echo "Error: no PROJECTSOURCE defined." ; exit 1 )
mkdir -p ~/bin [ -e ~/bin/update_automation ] || wget -O ~/bin/update_automation https://raw.github.com/SUSE-Cloud/automation/master/scripts/jenkins/update_automation && chmod a+x ~/bin/update_automation # fetch the latest automation updates update_automation track-upstream-and-package.pl
OBS_TYPE=${PROJECTSOURCE%%/*} OBS_PROJECT=${PROJECTSOURCE##*/}
case $OBS_TYPE in OBS) OSCAPI="https://api.opensuse.org" OSC_BUILD_ARCH=x86_64 case $OBS_PROJECT in *Folsom*) OSC_BUILD_DIST=SLE_11_SP2 ;; *) OSC_BUILD_DIST=SLE_11_SP3 ;; esac ;; *) echo "This jenkins instance only interacts with OBS." exit 1 ;; esac
fetch_primary_xml() { local repo=$1 disturl=$2 local host="http://download.opensuse.org/repositories/" test -n "$3" && host=$3 mkdir -p tmp/repo rm -rf tmp/repo/repomd.xml tmp/repo/primary.xml.gz curl -fs $host/$repo/$disturl/repodata/repomd.xml > tmp/repo/repomd.xml primary=$(grep "location.*primary.xml" tmp/repo/repomd.xml | cut -d'"' -f2) curl -Lfs $host/$repo/$disturl/$primary > tmp/repo/primary.xml.gz test -s tmp/repo/primary.xml.gz || { echo "failed to download primary.xml!"; rm -f tmp/repo/primary.xml.gz; exit 1; } rm -f tmp/repo/repomd.xml }
ARCH=x86_64
slashed_project=$(echo $project | sed -e 's,:,:\/,g')
fetch_primary_xml $slashed_project $repository installcheck $ARCH --withobsoletes --exclude "-kmp-" tmp/repo/primary.xml.gz --nocheck ~/repo/${repository}-${ARCH}.solv
